import {
  useState
} from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "./Expenses.css";

function Expenses(props) {
  const [filterYear, setFilterYear] = useState("2020");

  const saveFilterYear = (selectedYear) => {
    setFilterYear(selectedYear);
  };

  return ( <
    div >
    <
    Card className = "expenses" >
    <
    ExpensesFilter selected = {
      filterYear
    }
    onYearSelected = {
      saveFilterYear
    }
    /> {} <
    ExpenseItem title = {
      props.expenses[0].title
    }
    amount = {
      props.expenses[0].amount
    }
    date = {
      props.expenses[0].date
    }
    /> <
    ExpenseItem title = {
      props.expenses[1].title
    }
    amount = {
      props.expenses[1].amount
    }
    date = {
      props.expenses[1].date
    }
    /> <
    ExpenseItem title = {
      props.expenses[2].title
    }
    amount = {
      props.expenses[2].amount
    }
    date = {
      props.expenses[2].date
    }
    /> <
    /Card> <
    /div>
  );
}

export default Expenses;
